INSERT INTO `email_template` (`id`, `email_template001`, `email_template002`, `email_template003`, `serial_num`, `creator_role`, `created_by`, `creation_date`, `modified_by`, `modification_date`, `ip_address`, `record_status`) VALUES
('1361864988_1', 'New User Registration Welcome Message', '<p><span style="font-size: small;">Thank you for choosing Zidoff Webstore, the multichannel ecommerce product that enables you to leverage the power of Zidoff''s know-how, technology, and infrastructure to grow your business! Your Webstore can control all elements of your ecommerce business, including your branded website, in-store ordering, phone ordering, customer service needs, and fulfillment.</span><br /><span style="font-size: small;">We''re excited to help you get started. The next step is to complete your registration and account setup. To do this, you will need the following:</span></p>\r\n<p><br /><span style="font-size: small;"> &bull; Your business name, address and contact information.</span><br /><span style="font-size: small;"> &bull; A US-based credit card and billing address.</span><br /><span style="font-size: small;"> &bull; A phone number we can reach you at in the next few minutes</span></p>\r\n<p><span style="font-size: small;">Thank You.</span></p>', 'Welcome to Zidoff', 1, '', '', 0, '35991362173', 1425650531, 0, '1'),
('1361866429_1', 'Account Registration Verification', '<p><span>To verify your account and complete your registration process, click on the link above.</span></p>\r\n<p><span>Thank you for choosing Zidoff</span></p>', 'Verify your Zidoff account', 2, '', '', 0, '', 0, 0, '1'),
('1361869930_1', 'Lost Bid Notification', '<p>Your bid for the above listed product has been beaten. You will have to bid higher to win the auction.</p>\r\n<p>Thank you,</p>', 'Lost Bid', 3, '', '', 0, '', 0, 0, '1'),
('1361873946_1', 'Order Placement Invoice', '<p>You ordered the above listed items.</p>\r\n<p><strong>Note:</strong><span>&nbsp;Transaction would be cancelled if payment has not been received after 24hours</span></p>\r\n<p>&nbsp;</p>\r\n<p><span>Thank You.</span></p>', 'Zidoff Order Invoice', 4, '', '', 0, '', 0, 0, '1'),
('1361880649_1', 'Bank Account Verification Invoice', '<p>You are about to verify the above bank account which will activate your store and allow you start selling products on zidoff.com</p>\r\n<p>Thank You.</p>', 'Bank Account Verification Invoice', 5, '', '', 0, '', 0, 0, '1'),
('1361883485_1', 'Successful Listing', '<p>The above product has been listed and will become available to the general public after it has been reviewed within the next 24 hours</p>', 'Successful Product Listing', 6, '', '', 0, '', 0, 0, '1'),
('1361886974_1', 'Approved Listing Notification', '<p>Your product with the above listed features have been approved and can currently be seen and purchased by prospective customers on zidoff.com</p>\r\n<p>Thank You.</p>', 'Listed Product', 7, '', '', 0, '', 0, 0, '1'),
('1361887472_1', 'Disapproved Listing Notification', '<p>After review of the above listed item(s), we are unable to approve the listing due to violations of Zidoff Policy.</p>\r\n<p><span>Thus, the above item will not be available for sale on Zidoff.com</span></p>\r\n<p><a title="Click here for more information" href="http://www.zidoff.com/footer/support/" target="_blank"><span><span>Click here to get more information on reason for decline.</span></span></a></p>\r\n<p>Thank You.</p>', 'Disapproved Product', 8, '', '', 0, '', 0, 0, '1'),
('1361888937_1', 'Payment Verification', '<p>Payment for the above displayed transaction has been verified and the delivery agent has been contacted to pickup the items</p>', 'Verified Payment', 9, '', '', 0, '', 0, 0, '1'),
('1361893023_1', 'Successful Delivery Notification', '<p>The above listed items have been successfully delivered to the buyer.</p>\r\n<p>Thank You.</p>', 'Successful Delivery', 10, '', '', 0, '', 0, 0, '1'),
('1361893189_1', 'En-route', '<p>The above listed products have been picked up by the delivery agent and is currently being delivered</p>\r\n<p>Thank You</p>', 'En-route', 11, '', '', 0, '', 0, 0, '1'),
('1361893348_1', 'Returned Products Notification', '<p>The above listed products have been returned back to the merchant</p>\r\n<p>Thank You</p>', 'Returned Products', 12, '', '', 0, '', 0, 0, '1'),
('1361957413_1', 'Advert Payment Verification', '<p>Your payment for the above advertisement request has been verified and is now visible to the general public on zidoff.com</p>', 'Advert Payment Verification', 13, '', '', 0, '', 0, 0, '1'),
('1361958001_1', 'Featured Products Payment Verification', '<p>Your request for advertising the above listed product has been approved</p>\r\n<p>Thank You.</p>', 'Featured Product Payment Verification', 14, '', '', 0, '', 0, 0, '1'),
('1361964190_1', 'Admin Login Token', '<p>Login by clicking the link above, which contains your login token in order to grant you access to the admin panel.</p>\r\n<p>Note: the token will be live for only 15 minutes after which you will have to request for another</p>', 'Login Token', 15, '', '', 0, '', 0, 0, '1'),
('1362062082_1', 'Merchant Store Contact Email', '<p>The above mail was sent to you through the contact form on your merchant store.</p>\r\n<p>Thank you.</p>', 'Contact Us Form', 16, '', '', 0, '', 0, 0, '1'),
('1364220431_1', 'Auction Listing Payment Verification', '<p>Your request for auctioning the above listed product has been approved</p>\r\n<p>Thank You.</p>', 'Auction Listing Payment Verification', 17, '', '', 0, '', 0, 0, '1'),
('1364688808_1', 'Password Reset', '<p>Your password has been change to the password shown above.</p>\r\n<p>Note: please change this password after login</p>', 'Password Reset', 18, '', '', 0, '', 0, 0, '1'),
('1367602515_1', 'Custom Duty Payment', '<p>The above stated amount has been received as payment for import duties.</p>\r\n<p>Thank You.</p>', 'Import Duties Payment Confirmation', 19, '', '', 0, '', 0, 0, '1'),
('1368101694_1', 'Payment for Customized Banner', '<p>Payment for the upload of customized banners on your store front has been verified.</p>\r\n<p>You can now proceed with the upload of customized banners for your store front.</p>\r\n<p>Thank You.</p>', 'Customized Banners Payment Verification', 20, '', '', 0, '', 0, 0, '1'),
('1369310519_1', 'Won Bid from Auction', '<p>Congratulations, you have won the bid for the above listed product. The item has been added to your shopping cart. Until you have logged in and placed an order for the product the product can be purchased by other bidders.</p>\r\n<p>Thank you,</p>', 'Won Bid from Auction', 21, '', '', 0, '', 0, 0, '1'),
('1376494104', 'Successful Bank Account Verification', '<p>Your Bank Account has been successfully verified, you can start listing items on zidoff.com for sale after activating the bank account</p>', 'Successful Bank Account Verification', 22, '', '', 0, '', 0, 0, '1'),
('1376494105', 'Successful Store Set-up Fee Payment', '<p>Your Merchant Annual Store Subscription Fee has been successfully paid</p>\r\n<p>You can now set-up your store front on zidoff.com</p>', 'Successful Annual Store Subscription Fee Payment', 23, '', '', 0, '', 0, 0, '1'),
('1377423748_333', 'Pick-up Notification', '<p>Please pick-up the parcel with the above listed details.&nbsp;</p>\r\n<p><span style="font-size: medium; color: #ff0000;"><strong>NOTE: ITEMS BEING PICKED-UP MUST MATCH SPECIFICATIONS ON THE LABEL ABOVE, OTHERWISE IT SHOULD NOT BE PICKED-UP</strong></span></p>\r\n<p>Thank You.</p>', 'Zidoff Pick-up Notification', 24, '', '', 0, '', 0, 0, '1'),
('1377423921_340', 'DHL Shipping Label', '<p>Please PRINT the SHIPPING LABEL above and paste it on the package to be picked-up. A Courier from DHL will contact you shortly.</p>\r\n<p>Thank You.</p>', 'DHL Shipping Label', 25, '', '', 0, '', 0, 0, '1'),
('1377424368_895', 'FEDEX Shipping Label', '<p>1. Please PRINT the SHIPPING LABEL above and paste it on the package.</p>\r\n<p>2. Take the package with SHIPPING LABEL to your nearest FEDEX OFFICE and ship to the buyer whose details are stated in the LABEL.</p>\r\n<p>3. The cost incured when shipping will be reimbursed to you by Zidoff.com inaccordance with our <a title="Terms of Service" href="http://www.zidoff.com/footer/terms_of_agreement/" target="_blank">TERMS OF SERVICE</a>&nbsp;</p>\r\n<p>Thank You.</p>', 'FEDEX Shipping Label', 26, '', '', 0, '', 0, 0, '1'),
('1381750799_970', 'China Post Shipping Label', '<p>1. Please PRINT the SHIPPING LABEL above and paste it on the package.</p>\r\n<p>2. Take the package with SHIPPING LABEL to your nearest CHINA POST OFFICE and ship to the buyer whose details are stated in the LABEL.</p>\r\n<p>3. The cost incured when shipping will be reimbursed to you by Zidoff.com inaccordance with our&nbsp;<a title="Terms of Service" href="http://www.zidoff.com/footer/terms_of_agreement/" target="_blank">TERMS OF SERVICE</a>&nbsp;</p>\r\n<p>Thank You.</p>', 'China Post Shipping Label', 27, '', '', 0, '', 0, 0, '1'),
('1381751040_101', 'NIPOST Shipping Label', '<p>Please PRINT the SHIPPING LABEL above and paste it on the package to be picked-up. A Courier from NIPOST will contact you shortly.</p>\r\n<p>Thank You.</p>', 'NIPOST Shipping Label', 28, '', '', 0, '', 0, 0, '1'),
('1381751104_400', 'Custom Shipping Label', '<p>1. Please PRINT the SHIPPING LABEL above and paste it on the package.</p> <p>2. Ship the package with SHIPPING LABEL to the buyer whose details are stated in the LABEL.</p> <p>Thank You.</p>', 'Zidoff.com Merchant Custom Shipping Label', 29, '', '', 0, '', 0, 0, '1'),
('1381751104_412', 'USPS Shipping Label', '<p>1. Please PRINT the SHIPPING LABEL above and paste it on the package.</p>\r\n<p>2. Take the package with SHIPPING LABEL to your nearest USPS OFFICE and ship to the buyer whose details are stated in the LABEL.</p>\r\n<p>3. The cost incured when shipping will be reimbursed to you by Zidoff.com inaccordance with our&nbsp;<a title="Terms of Service" href="http://www.zidoff.com/footer/terms_of_agreement/" target="_blank">TERMS OF SERVICE</a>&nbsp;</p>\r\n<p>Thank You.</p>', 'USPS Shipping Label', 30, '', '', 0, '', 0, 0, '1'),
('1389047731_766', 'Successful Email Address Verification', '<p><span style="color: #993300; font-size: small;"><strong>Congratulations!!!</strong></span></p>\r\n<p>You''ve successfully completed your registration process on zidoff.com by verifying your email address.</p>\r\n<p>&nbsp;</p>\r\n<p>You can always upgrade to a seller at any time by clicking on the <a title="Become A Seller" href="http://www.zidoff.com/account/?account_a=become_a_seller&amp;g=1&amp;m=account" target="_blank">Become a Seller</a> menu in your account panel.</p>\r\n<p>&nbsp;</p>', 'Registration Complete', 31, '', '', 0, '', 0, 0, '1'),
('1392897839_364', 'Support Auto Responder', '<p>Your request has been received and is being treated.</p>\r\n<p>One of our customer care agents will reply you shortly.</p>\r\n<p>Thank You.</p>', 'Support Ticket', 32, '', '', 0, '', 0, 0, '1'),
('1407380913_502', 'Payment Failed', '<p><span>Payment for the above displayed transaction failed</span></p>', 'Failed Transaction', 33, '', '', 0, '', 0, 0, '1'),
('1407380985_117', 'Pending Transaction Status', '<p><span>Payment for the above displayed transaction is still being processed and you will receive an email once processing has been completed</span></p>', 'Pending Transaction Status', 34, '', '', 0, '', 0, 0, '1'),
('1407380985_118', 'User Password Change Alert', '<p><span>Your Password on Zidoff.com has been successfully changed, Please reset the current password if this action was not triggered by you</span></p>', 'User Password Change Alert', 35, '', '', 0, '', 0, 0, '1');
